import React, { useEffect, useState } from 'react';
import Axios from "axios";
import CartButtom from './CartButtom';
import { useSelector, useDispatch } from 'react-redux';
import RightTable from './rightTable';

import data from '../data';

function MainScreen(props) {

    const token = "tTU3gFVUdP";
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };

    useEffect(() => {
        dispatch(callAPI("t.raman@iiitmanipur.ac.in"));
        return () => {
        //
        };
    }, []);

    const dispatch = useDispatch();
    const [filter, setFilter] = useState('DEL');
    const [apiData,setApiData] = useState([]);

    const callAPI = (email) => async(dispatch)=> {
        try {
            const api = await Axios.post(
                "https://f0ztti2nsk.execute-api.ap-south-1.amazonaws.com/v1/consignment/fetch",
                {email : email},
                config
            );
            if(api){
                setApiData(api.data);
               console.log(apiData);
            } else {

            }

        } catch (error) {

        }
    }


    let DEL = []
    let INT = []
    let OOD = []
    let DEX = []
    let NFI = []
    console.log(apiData)
    for (let index = 0; index < apiData.length; index++) {
        if(apiData[index]["current_status_code"]==="DEL"){
            DEL.push(apiData[index]);
        } else if(apiData[index]["current_status_code"]==="INT"){
            INT.push(apiData[index]);
        } else if(apiData[index]["current_status_code"]==="OOD"){
            OOD.push(apiData[index]);
        } else if(apiData[index]["current_status_code"]==="DEX"){
            DEX.push(apiData[index]);
        } else {
            NFI.push(apiData[index]);
        }
    }
    const apis = OOD;
    const submitHandler = (e) => {
        setFilter("")
    }

    return (
        <>
        <div className="container">
            <div className="row cart-set">
                <CartButtom 
                onClick={submitHandler}
                    data={{
                        "counter": DEL.length,
                        "text": "DEL"
                    }}/>
                <CartButtom data={{
                    "counter": INT.length,
                    "text": "INT"
                }}/>
                <CartButtom data={{
                    "counter": OOD.length,
                    "text": "OOD"
                }}/>
                <CartButtom data={{
                    "counter": DEX.length,
                    "text": "DEX"
                }}/>
                <CartButtom data={{
                    "counter": NFI.length,
                    "text": "NFI"
                }}/>
            </div>
        </div>
        <RightTable data={apis}/>
        </>
    );
}

export default  MainScreen;
